iv class="col-md-4 col-sm-12"></div>    						<?php if ($sede == 0) { ?>    						<div class="col-md-2 col-sm-12" style="padding-top: 25px;">    							<button type="button" id="btnNuevo" class="btn btn-primary" title="Nuevo" data-toggle="modal" data-target="#exampleModal"><em class="fa fa-pencil-square-o"></em></button>    							<script type="text/javascript">        							$("#btnBuscar").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=newcampus";            		    			});            		    		</script>    						</div>    						<?php } ?>    					</div>            		</form>					<div class="table-responsive">        				<div class="box-body">        					<table class="table table-bordered table-hover datatable table-nowrap">        						<thead>      								<tr>        								<th scope="col">Código</th>        								<th scope="col">Nombre</th>        								<th scope="col">Dirección</th>        								<th scope="col">Pisos</th>        								<th scope="col">Estado</th>         								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
