                                ?>
        							<tr>            							<td style="text-align: left;"><?php echo str_pad($objSede->id, 8, "0", STR_PAD_LEFT); ?></td>            							<td style="text-align: left;"><?php echo $objSede->nombre; ?></td>            							<td style="text-align: left;"><?php echo $objSede->direccion; ?></td>            							<td style="text-align: left;"><?php echo $detalle; ?></td>            							<td style="text-align: left;"><?php echo $objEstado->nombre; ?></td>            							<td style="text-align: left;">            								<?php if ($objEstado->opcion) { ?>            								<a href="index.php?view=newcampus&id=<?php echo $objSede->id; ?>" title="Editar" class="btn btn-warning btn-xs"><em class="fa fa-pencil-square-o"></em></a>            								<a href="index.php?view=newcampustable&id=<?php echo $objSede->id; ?>" title="Asociar Mesas" class="btn btn-success btn-xs">AM</a>            								<a href="index.php?view=newcampusarea&id=<?php echo $objSede->id; ?>" title="Asociar Areas" class="btn btn-info btn-xs">AA</a>            								<a id="lnkdel<?php echo $objSede->id; ?>" title="Anular" class="btn btn-danger btn-xs"><em class="fa fa-trash"></em></a>            								<script type="text/javascript">                								$("#lnkdel<?php echo $objSede->id; ?>").click(function() {                        							Swal.fire({                            							title: "Desea anular la sede <?php echo $objSede->nombre; ?>",                        								icon: "warning",                        								showCancelButton: true,                        								confirmButtonColor: "#3085d6",                        								cancelButtonColor: "#d33",                        								confirmButtonText: "Anular",                        								cancelButtonText: "Cancelar"                        							}).then((result) => {                        								if (result.isConfirmed) {                        									$.ajax({                        		    		    				type: "POST",                        		    		    				url: "./index.php?action=addcampus",                        		    		    				dataType: "html",                        		    		    				data: "id=<?php echo $objSede->id; ?>&accion=2",                        		    		    				beforeSend: function() {                        		    		    					$("#lnkdel<?php echo $objSede->id; ?>").attr("disabled","disabled");                        		    		    					$.blockUI();                        		    		    				},                        		    		    				success: function(data) {                        		    		        				if (data > 0) {                        		    		        					Swal.fire({                        		    		                                icon: "success",                        		    		                                title: "Se anuló correctamente la sede <?php echo $objSede->nombre; ?>",                        		    										showCancelButton: false,                        		    										confirmButtonColor: "#3085d6",                        		    										confirmButtonText: "OK"                        		    		                        	}).then((result) => {                        		    										window.location.href = "./index.php?view=campus";                        		    		                        	})                        		    		        				} else {                        		    		        					Swal.fire({                        		    		    							icon: "error",                        		    		    							title: "Ocurrio un error al anular la sede <?php echo $objSede->nombre; ?>"                        		    		    						})                        		    		        				}                        		    		    				},                        		    		    				error: function(data) {                        		    		    					Swal.fire({                        		    									icon: "error",                        		    									title: "Ocurrio un error al anular la sede <?php echo $objSede->nombre; ?>"                        		    								})                        		    		    				},                        		    		    				complete: function(data) {                        		    		    					$("#lnkdel<?php echo $objSede->id; ?>").removeAttr("disabled");                        		    		    					$.unblockUI();                        		    		    				}                        		    		    			});                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
        						<?php
                                    }
                                ?>                                </tbody>
        					</table>        				</div>        			</div>        		</div>        	</div>
