<?php    $estado = "1";    if (count($_POST) > 0) {        $estado = $_POST["estado"];    }    $lstEstado = EstadoData::getAll();    $lstModulo = ModuloData::getAll($estado);?><section class="content">	<div class="row">		<div class="col-md-12">			<div class="panel panel-primary">    			<div class="panel-heading" style="background-color: #3c8dbc;"><h3 class="panel-title">Módulo</h3></div>    			<div class="panel-body">    				<form class="form-horizontal" method="post" id="modules" action="" autocomplete="off">    			            			<div class="form-group">            				<div class="col-md-2 col-sm-12">    							<label for="estado">Estado :</label>    							<select id="estado" name="estado" class="form-control">    								<option value="">TODOS</option>    								<?php foreach ($lstEstado as $objEstado) { ?>    								<option value="<?php echo $objEstado->id; ?>" <?php if ($objEstado->id == $estado) { echo "selected"; } ?>><?php echo $objEstado->nombre; ?></option>      								<?php } ?>    							</select>    						</div>    						<div class="col-md-10 col-sm-12" style="padding-top: 25px;">        						<button type="button" id="btnBuscar" class="btn btn-success" title="Buscar"><em class="fa fa-search"></em></button>        						<button type="button" id="btnLimpiar" class="btn btn-danger" title="Limpiar"><em class="fa fa-eraser"></em></button>        						<button type="button" id="btnNuevo" class="btn btn-primary" title="Nuevo"><em class="fa fa-pencil-square-o"></em></button>        						<script type="text/javascript">        							$("#btnBuscar").click(function(){            							$.blockUI();            		    				$("button").prop("disabled", true);            		    				$("#modules").submit();            		    			});            						$("#btnLimpiar").click(function(){            						            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=modules";            		    			});            		    			$("#btnNuevo").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=newmodule";            		    			});        						</script>            				</div>    					</div>            		</form>					<div class="table-responsive">        				<div class="box-body">        					<table class="table table-bordered table-hover datatable table-nowrap">        						<thead>        							<tr>        								<th scope="col">Módulo Padre</th>        								<th scope="col">Orden</th>        								        								<th scope="col">Icono</th>        								<th scope="col">Nombre</th>        								<th scope="col">URL</th>        								<th scope="col">Estado</th>        								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
        						<?php        						      $moduloPadre = "";         						      foreach ($lstModulo as $objModulo) {        						                  						          $objModuloPadre = $objModulo->getModuloPadre();        						          if ($objModuloPadre) {        						              $moduloPadre = $objModuloPadre->nombre;        						          } else {        						              $moduloPadre = "";        						          }        						          $objEstado = $objModulo->getEstado();
                                ?>
        							<tr>            							<td style="text-align: left;"><?php echo $moduloPadre; ?></td>            							<td style="text-align: left;"><?php echo $objModulo->orden; ?></td>            							            							<td style="text-align: left;"><?php echo $objModulo->icono; ?></td>            							<td style="text-align: left;"><?php echo $objModulo->nombre; ?></td>            							<td style="text-align: left;"><?php echo $objModulo->url; ?></td>            							<td style="text-align: left;"><?php echo $objEstado->nombre; ?></td>            							<td>            								<?php if ($objEstado->opcion) { ?>            								<a href="index.php?view=newmodule&id=<?php echo $objModulo->id; ?>" class="btn btn-warning btn-xs" title="Editar"><em class="fa fa-pencil-square-o"></em></a>            								<a id="lnkdel<?php echo $objModulo->id; ?>" title="Anular" class="btn btn-danger btn-xs"><em class="fa fa-trash"></em></a>            								<script type="text/javascript">                        						$("#lnkdel<?php echo $objModulo->id; ?>").click(function() {                        							Swal.fire({                            							title: "Desea anular el módulo <?php echo $objModulo->nombre; ?>",                        								icon: "warning",                        								showCancelButton: true,                        								confirmButtonColor: "#3085d6",                        								cancelButtonColor: "#d33",                        								confirmButtonText: "Anular",                        								cancelButtonText: "Cancelar"                        							}).then((result) => {                        								if (result.isConfirmed) {                        									$.ajax({                        		    		    				type: "POST",                        		    		    				url: "./index.php?action=addmodule",                        		    		    				dataType: "html",                        		    		    				data: "id=<?php echo $objModulo->id; ?>&accion=2",                        		    		    				beforeSend: function() {                        		    		    					$("#lnkdel<?php echo $objModulo->id; ?>").attr("disabled","disabled");                        		    		    					$.blockUI();                        		    		    				},                        		    		    				success: function(data) {                        		    		        				if (data > 0) {                        		    		        					Swal.fire({                        		    		                                icon: "success",                        		    		                                title: "Se anuló correctamente el módulo <?php echo $objModulo->nombre; ?>",                        		    										showCancelButton: false,                        		    										confirmButtonColor: "#3085d6",                        		    										confirmButtonText: "OK"                        		    		                        	}).then((result) => {                        		    										window.location.href = "./index.php?view=modules";                        		    		                        	})                        		    		        				} else {                        		    		        					Swal.fire({                        		    		    							icon: "warning",                        		    		    							title: "Ocurrio un error al anular el módulo <?php echo $objModulo->nombre; ?>"                        		    		    						})                        		    		        				}                        		    		    				},                        		    		    				error: function(data) {                        		    		    					Swal.fire({                        		    									icon: "error",                        		    									title: "Ocurrio un error al anular el módulo <?php echo $objModulo->nombre; ?>"                        		    								})                        		    		    				},                        		    		    				complete: function(data) {                        		    		    					$("#lnkdel<?php echo $objModulo->id; ?>").removeAttr("disabled");                        		    		    					$.unblockUI();                        		    		    				}                        		    		    			});                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
        						<?php
                                    }
                                ?>                                </tbody>
        					</table>        				</div>        			</div>        		</div>        	</div>
		</div>	</div></section>