<?php    $tabla = "";    $estado = "1";    if (count($_POST) > 0) {        $estado = $_POST["estado"];        $tabla = $_POST["tabla"];    }    $lstEstado = EstadoData::getAll();    $lstTabla = TablaData::getAll(1);    $lstParametro = ParametroData::getAll($estado, $tabla);    ?><section class="content">	<div class="row">		<div class="col-md-12">			<div class="panel panel-primary">    			<div class="panel-heading" style="background-color: #3c8dbc;"><h3 class="panel-title">Parámetro</h3></div>    			<div class="panel-body">    				<form class="form-horizontal" method="post" id="parameters" action="" role="form" autocomplete="off">    			            			<div class="form-group">            				<div class="col-md-4 col-sm-12">    							<label for="tabla">Tabla :</label>    							<select id="tabla" name="tabla" class="form-control">    								<option value="">TODOS</option>    								<?php foreach ($lstTabla as $objTabla) { ?>    								<option value="<?php echo $objTabla->nombre; ?>" <?php if ($objTabla->nombre == $tabla) { echo "selected"; } ?>><?php echo $objTabla->nombre; ?></option>      								<?php } ?>    							</select>    						</div>            				<div class="col-md-2 col-sm-12">    							<label for="estado">Estado :</label>    							<select id="estado" name="estado" class="form-control">    								<option value="">TODOS</option>    								<?php foreach ($lstEstado as $objEstado) { ?>    								<option value="<?php echo $objEstado->id; ?>" <?php if ($objEstado->id == $estado) { echo "selected"; } ?>><?php echo $objEstado->nombre; ?></option>      								<?php } ?>    							</select>    						</div>    						<div class="col-md-6 col-sm-12" style="padding-top: 25px;">        						<button type="submit" id="btnBuscar" class="btn btn-success" title="Buscar"><em class="fa fa-search"></em></button>        						<button type="button" id="btnLimpiar" class="btn btn-danger" title="Limpiar"><em class="fa fa-eraser"></em></button>        						<a href="index.php?view=newparameter" class="btn btn-primary" title="Nuevo"><em class="fa fa-pencil-square-o"></em></a>        						<script type="text/javascript">            						$("#btnLimpiar").click(function(){            							$.blockUI();            		    				location.href = "./index.php?view=parameters";            		    			});        						</script>            				</div>    						    					</div>            		</form>					<div class="table-responsive">        				<div class="box-body">        					<table class="table table-bordered table-hover datatable table-nowrap">        						<thead>        							<tr>        								<th scope="col">Código</th>        								<th scope="col">Tabla</th>        								<th scope="col">Nombre</th>        								<th scope="col">Valor</th>        								<th scope="col">Estado</th>        								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
        						<?php        						    foreach ($lstParametro as $objParametro) {        						        $objEstado = $objParametro->getEstado();        						        $objTabla = $objParametro->getTabla();
                                ?>
        							<tr>            							<td style="text-align: left;"><?php echo $objParametro->id; ?></td>            							<td style="text-align: left;"><?php echo $objTabla->nombre; ?></td>            							<td style="text-align: left;"><?php echo $objParametro->nombre; ?></td>            							<td style="text-align: left;"><?php echo $objParametro->valor1; ?></td>            							<td style="text-align: left;"><?php echo $objEstado->nombre; ?></td>            							<td style="text-align: left;">            								<?php if ($objEstado->opcion) { ?>            								<!--<a href="" class="btn btn-warning btn-xs" data-toggle="modal" data-target="#exampleModal<?php echo $objParametro->id; ?>" title="Editar"><em class="fa fa-pencil-square-o"></em></a>-->            								<a href="index.php?view=newparameter&id=<?php echo $objParametro->id; ?>" title="Editar" class="btn btn-warning btn-xs"><em class="fa fa-pencil-square-o"></em></a>            								<a id="lnkdel<?php echo $objParametro->id; ?>" title="Anular" class="btn btn-danger btn-xs"><em class="fa fa-trash"></em></a>            								<script type="text/javascript">                								$("#lnkdel<?php echo $objParametro->id; ?>").click(function() {                        							Swal.fire({                            							title: "Desea anular el parámetro <?php echo $objParametro->nombre; ?>",                        								icon: "warning",                        								showCancelButton: true,                        								confirmButtonColor: "#3085d6",                        								cancelButtonColor: "#d33",                        								confirmButtonText: "Anular",                        								cancelButtonText: "Cancelar"                        							}).then((result) => {                        								if (result.isConfirmed) {                        									$.ajax({                        		    		    				type: "POST",                        		    		    				url: "./index.php?action=addparameter",                        		    		    				async: true,                        		    		    				dataType: "html",                        		    		    				data: "id=<?php echo $objParametro->id; ?>&accion=2",                        		    		    				beforeSend: function() {                        		    		    					$("#lnkdel<?php echo $objParametro->id; ?>").attr("disabled","disabled");                        		    		    					$.blockUI();                        		    		    				},                        		    		    				success: function(data) {                        		    		        				if (data > 0) {                        		    		        					Swal.fire({                        		    		                                icon: "success",                        		    		                                title: "Se anuló correctamente el parámetro <?php echo $objParametro->nombre; ?>",                        		    										showCancelButton: false,                        		    										confirmButtonColor: "#3085d6",                        		    										confirmButtonText: "OK"                        		    		                        	}).then((result) => {                        		    										window.location.href = "./index.php?view=parameters";                        		    		                        	})                        		    		        				} else {                        		    		        					Swal.fire({                        		    		    							icon: "warning",                        		    		    							title: "Ocurrio un error al anular el parámetro <?php echo $objParametro->nombre; ?>"                        		    		    						})                        		    		        				}                        		    		    				},                        		    		    				error: function(data) {                        		    		    					Swal.fire({                        		    									icon: "error",                        		    									title: "Ocurrio un error al anular el parámetro <?php echo $objParametro->nombre; ?>"                        		    								})                        		    		    				},                        		    		    				complete: function(data) {                        		    		    					$("#lnkdel<?php echo $objParametro->id; ?>").removeAttr("disabled");                        		    		    					$.unblockUI();                        		    		    				}                        		    		    			});                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
