iv class="col-md-4 col-sm-12" style="padding-top: 25px;">        						<button type="button" id="btnBuscar" class="btn btn-success" title="Buscar"><em class="fa fa-search"></em></button>        						<button type="button" id="btnLimpiar" class="btn btn-danger" title="Limpiar"><em class="fa fa-eraser"></em></button>        						<button type="button" id="btnNuevo" class="btn btn-primary" title="Nuevo"><em class="fa fa-pencil-square-o"></em></button>        						<script type="text/javascript">        							$("#btnBuscar").click(function(){            							$.blockUI();            		    				$("button").prop("disabled", true);            		    				$("#products").submit();            		    			});            						$("#btnLimpiar").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				$.post("index.php?action=deletesearch", {                							opcion: "products"                                        }, function (data) {                                        	location.href = "./index.php?view=products";                                        });            		    			});            		    			$("#btnNuevo").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=newproduct";            		    			});        						</script>            				</div>    					</div>            		</form>					<div class="table-responsive">        				<div class="box-body">        					<table class="table table-bordered table-hover datatable table-nowrap">        						<thead>        							<tr>        								<th scope="col">Código</th>        								<th scope="col">Categoría</th>        								<th scope="col">Tipo</th>        								<th scope="col">Nombre</th>        								<th scope="col">Precio Costo</th>        								<th scope="col">Precio Venta Normal</th>        								<th scope="col">Precio Venta Especial</th>        								<th scope="col">Estado</th>        								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
