ed) {                        									$.ajax({                        									    type: "post",                        									    url: "./?action=addproduct",                        									    data: "id=<?php echo $objProducto->id; ?>&accion=2",                        									    dataType: "html",                        									    beforeSend: function() {                        									    	$("#lnkdel<?php echo $objProducto->id; ?>").attr("disabled","disabled");                        		    		    					$.blockUI();                        		    		    				},                        									    success: function(data) {                        									        if (data > 0) {                        									        	Swal.fire({                                    		                                icon: "success",                                    		                                title: "Se anuló correctamente el producto <?php echo $objProducto->nombre; ?>",                                    										showCancelButton: false,                                    										confirmButtonColor: "#3085d6",                                    										confirmButtonText: "OK"                                    		                        	}).then((result) => {                                    										window.location.href = "./index.php?view=products";                                    		                        	})                        									        } else {                        									        	Swal.fire({                        		    		    							icon: "warning",                        		    		    							title: "Ocurrio un error al anular el producto"                        		    		    						})                        										    }                        									    },                        									    error: function() {                        									    	Swal.fire({                        	    		    							icon: "error",                        	    		    							title: "Ocurrio un error al anular el producto"                        	    		    						})                        									    },                        		    		    				complete: function(data) {                        		    		    					$("#lnkdel<?php echo $objProducto->id; ?>").removeAttr("disabled");                        		    		    					$.unblockUI();                        		    		    				}                        									});                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
