<?php    $sede = $_SESSION["sede"];    $estado = "1";    if (count($_POST) > 0) {        $estado = $_POST["estado"];    }        $lstEstado = EstadoData::getAll();        $lstReceta = RecetaData::getAll($estado, $sede);?><section class="content">	<div class="row">		<div class="col-md-12">			<div class="panel panel-primary">    			<div class="panel-heading" style="background-color: #3c8dbc;"><h3 class="panel-title">Recetas</h3></div>    			<div class="panel-body">    				<form class="form-horizontal" method="post" id="recipes" action="" role="form" autocomplete="off">    			            			<div class="form-group">            				<div class="col-md-2 col-sm-12">    							<label for="estado">Estado :</label>    							<select id="estado" name="estado" class="form-control">    								<option value="">TODOS</option>    								<?php foreach ($lstEstado as $objEstado) { ?>    								<option value="<?php echo $objEstado->id; ?>" <?php if ($objEstado->id == $estado) { echo "selected"; } ?>><?php echo $objEstado->nombre; ?></option>      								<?php } ?>    							</select>    						</div>    						<div class="col-md-10 col-sm-12" style="padding-top: 25px;">        						<button type="button" id="btnBuscar" class="btn btn-success" title="Buscar"><em class="fa fa-search"></em></button>        						<button type="button" id="btnLimpiar" class="btn btn-danger" title="Limpiar"><em class="fa fa-eraser"></em></button>        						<button type="button" id="btnNuevo" class="btn btn-primary" title="Nuevo"><em class="fa fa-pencil-square-o"></em></button>        						<script type="text/javascript">        							$("#btnBuscar").click(function(){            							$.blockUI();            		    				$("button").prop("disabled", true);            		    				$("#products").submit();            		    			});            						$("#btnLimpiar").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=recipes";                                                    		    			});            		    			$("#btnNuevo").click(function(){            							$.blockUI();            							$("button").prop("disabled", true);            		    				location.href = "./index.php?view=newrecipe";                                                    		    			});        						</script>            				</div>    						    					</div>            		</form>					<div class="table-responsive">        				<div class="box-body">        					<table class="table table-bordered table-hover datatable table-nowrap">        						<thead>        							<tr>        								<th scope="col">Código</th>        								<th scope="col">Producto</th>        								<th scope="col" style="text-align: right;">Costo</th>        								<th scope="col" style="text-align: right;">Precio Venta</th>        								<th scope="col">Estado</th>        								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
        						<?php        						    foreach ($lstReceta as $objReceta) {        						        $objEstado = $objReceta->getEstado();        						        $objProducto = $objReceta->getProducto();
                                ?>
        							<tr>            							<td style="text-align: left;"><?php echo str_pad($objReceta->id, 8, "0", STR_PAD_LEFT); ?></td>            							<td style="text-align: left;"><?php echo $objProducto->nombre; ?></td>            							<td style="text-align: right;"><?php echo number_format($objReceta->costo,2); ?></td>            							<td style="text-align: right;"><?php echo number_format($objProducto->precio1,2); ?></td>            							<td style="text-align: left;"><?php echo $objEstado->nombre; ?></td>            							<td style="text-align: left;">            								<?php if ($objEstado->opcion) { ?>            								<a href="index.php?view=newrecipe&id=<?php echo $objReceta->id; ?>" title="Editar" class="btn btn-warning btn-xs"><em class="fa fa-pencil-square-o"></em></a>            								<a id="lnkdel<?php echo $objReceta->id; ?>" title="Anular" class="btn btn-danger btn-xs"><em class="fa fa-trash"></em></a>            								<script type="text/javascript">                								$("#lnkdel<?php echo $objReceta->id; ?>").click(function() {                									$("#lnkdel<?php echo $objReceta->id; ?>").attr("disabled","disabled");                        							Swal.fire({                            							title: "Desea anular la receta asociada al producto <?php echo $objProducto->nombre; ?>",                        								icon: "warning",                        								showCancelButton: true,                        								confirmButtonColor: "#3085d6",                        								cancelButtonColor: "#d33",                        								confirmButtonText: "Anular",                        								cancelButtonText: "Cancelar"                        							}).then((result) => {                        								if (result.isConfirmed) {                        									$.ajax({                        		    		    				type: "POST",                        		    		    				url: "./index.php?action=addrecipe",                        		    		    				dataType: "html",                        		    		    				data: "id=<?php echo $objReceta->id; ?>&accion=2",                        		    		    				beforeSend: function() {                        		    		    					$("#lnkdel<?php echo $objReceta->id; ?>").attr("disabled","disabled");                        		    		    					$.blockUI();                        		    		    				},                        		    		    				success: function(data) {                        		    		        				if (data > 0) {                        		    		        					Swal.fire({                        		    		                                icon: "success",                        		    		                                title: "Se anuló correctamente la receta asociada al producto <?php echo $objProducto->nombre; ?>",                        		    										showCancelButton: false,                        		    										confirmButtonColor: "#3085d6",                        		    										confirmButtonText: "OK"                        		    		                        	}).then((result) => {                        		    										window.location.href = "./index.php?view=recipes";                        		    		                        	})                        		    		        				} else {                        		    		        					Swal.fire({                        		    		    							icon: "warning",                        		    		    							title: "Ocurrio un error al anular la receta asociada al producto <?php echo $objProducto->nombre; ?>"                        		    		    						})                        		    		        				}                        		    		    				},                        		    		    				error: function(data) {                        		    		    					Swal.fire({                        		    									icon: "error",                        		    									title: "Ocurrio un error al anular la receta asociada al producto <?php echo $objProducto->nombre; ?>"                        		    								})                        		    		    				},                        		    		    				complete: function(data) {                        		    		    					$("#lnkdel<?php echo $objReceta->id; ?>").removeAttr("disabled");                        		    		    					$.unblockUI();                        		    		    				}                        		    		    			});                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
        						<?php
                                    }
                                ?>                                </tbody>
        					</table>        				</div>        			</div>        		</div>        	</div>
		</div>	</div></section>