red table-hover datatable table-nowrap">        						<thead>        							<tr>        								<th scope="col">Código</th>        								<th scope="col">Nombre</th>        								<th scope="col">Estado</th>        								<th scope="col">Acciones</th>        							</tr>        						</thead>        						<tbody>
        						<?php        						    foreach ($lstSubProducto as $objSubProducto) {        						        $objEstado = $objSubProducto->getEstado();
                                ?>
        							<tr>            							<td style="text-align: left;"><?php echo $objSubProducto->id; ?></td>            							<td style="text-align: left;"><?php echo $objSubProducto->nombre; ?></td>            							<td style="text-align: left;"><?php echo $objEstado->nombre; ?></td>            							<td style="text-align: left;">            								<?php if ($objEstado->opcion) { ?>            								<a href="index.php?view=newsubproduct&id=<?php echo $objSubProducto->id; ?>" title="Editar" class="btn btn-warning btn-xs">E</a>            								<a id="lnkdel<?php echo $objSubProducto->id; ?>" title="Anular" class="btn btn-danger btn-xs">A</a>            								<script type="text/javascript">                        						$("#lnkdel<?php echo $objSubProducto->id; ?>").click(function() {                        							Swal.fire({                            							title: 'Desea anular el subproducto <?php echo $objSubProducto->nombre; ?>',                        								text: "",                        								icon: 'warning',                        								showCancelButton: true,                        								confirmButtonColor: '#3085d6',                        								cancelButtonColor: '#d33',                        								confirmButtonText: 'Anular'                        							}).then((result) => {                        								if (result.isConfirmed) {                        									$.ajax({                        									    type: "post",                        									    url: "./?action=addsubproduct",                        									    data: "id="+<?php echo $objSubProducto->id; ?>+"&accion=2",                        									                            									    dataType: "html",                        									    success: function(data) {                        									        if (data > 0) {                        									        	Swal.fire({                                    		                                icon: 'success',                                    		                                title: 'Se anuló correctamente el subproducto <?php echo $objSubProducto->nombre; ?>',                                    										showCancelButton: false,                                    										confirmButtonColor: '#3085d6',                                    										confirmButtonText: 'OK'                                    		                        	}).then((result) => {                                    										if (result.value) {                                    											window.location.href = "./index.php?view=subproducts";                                    										}                                    		                        	})                        									        } else {                        									        	Swal.fire({                        		    		    							icon: 'error',                        		    		    							title: 'Ocurrio un error al anular el subproducto'                        		    		    						})                        										    }                        									    },                        									    error: function() {                        									    	Swal.fire({                        	    		    							icon: 'error',                        	    		    							title: 'Ocurrio un error al anular el subproducto'                        	    		    						})                        									    }                        									});                        									                        								}                        							})                        						});                        					</script>                        					<?php } ?>            							</td>        							</tr>
